      <Grid.Item xs="12" sm="auto">        
        <button onClick={() => show('lg')}>          Large dialog</button>        
      </Grid.Item>      
      <Grid.Item xs="12" sm="auto">        
        <button onClick={() => show('xl')}>          Extra large dialog</button>        
      </Grid.Item>      
    </Grid>    
    <Dialog animation="fade" open={open} size={size} onClose={() => hide()}>      
